INSERT INTO `uk_templet` VALUES ('4028811b6191e289016191e4721c033d', '数据表', null, 'report', null, '2018-02-14 09:18:43', null, null, '<#if reportData??>\r\n<table class=\"layui-table ukefu-report\">\r\n  <thead>\r\n	<#if reportData?? && reportData.col?? && reportData.col.title??>\r\n	  <#list reportData.col.title as tlist>\r\n		<tr class=\"sortableTr\">\r\n		  <#if reportData.col.title?size gt 1 && tlist_index==0 && reportData.row.title?size gt 0>\r\n			<td align=\"center\" colspan=\"${reportData.row.title?size}\" rowspan=\"${reportData.col.title?size-1}\" class=\"s_blue\"></td>\r\n		  </#if>\r\n		  <#if (tlist_index+1)==reportData.col.title?size && reportData.row?? && reportData.row.firstTitle??>\r\n			<#list reportData.row.firstTitle as first>\r\n			  <td align=\"center\" data-title=\"${first.name?url}\" data-flag=\"dim\">${first.rename!first.name!\'\'}				\r\n			</#list>\r\n		  </#if>\r\n		  <#if tlist??>\r\n			<#list tlist as tl>\r\n			  <td align=\"center\" <#if tl.leveltype?? && tl.leveltype==\"newcol\">rowspan=\"${reportData.col.title?size}\"</#if>colspan=\"${tl.colspan}\" > ${tl.rename!tl.name!\"\"}\r\n				</td>\r\n			</#list>\r\n			</#if>\r\n	</tr>\r\n	</#list>\r\n	</#if>\r\n  </thead>\r\n  <tbody>\r\n	<#if reportData?? && reportData.data??>\r\n	  <#list reportData.data as values>\r\n		<tr class=\"rowcell\">\r\n		  <#if reportData.row?? && reportData.row.title?? && reportData.row.title?size gt 0>\r\n			<#list reportData.row.title as tl>\r\n			  <#assign rows=0>\r\n				<#list tl as title>\r\n				  <#if title??>\r\n					<#if rows==values_index && title.name !=\"TOTAL_TEMP\">\r\n					  	<#if title.leveltype !=\"newrow_extend_sum\">\r\n						<td nowrap=\"nowrap\" align=\"center\" class=\"blue_k <#if title.total == true>total</#if>\" rowspan=\"${title.rowspan!\'0\'}\" <#if title.colspan gt 1>colspan=\"${title.colspan}\"</#if>>\r\n						  ${title.formatName!\'\'}</td>\r\n						</#if>\r\n						<#if title.valueData??>\r\n						  <#list title.valueData as value>\r\n							<#if value.merge==false>\r\n							  <td rowspan=\"${value.rowspan}\" colspan=\"${value.colspan}\" align=\"center\" class=\"measure ${value.vtclass!\'\'}\" nowrap=\"nowrap\" <#if value.cellmergeid??>data-cellmerge=\"${value.cellmergeid}\"</#if>>\r\n							${value.valueStyle!value.foramatValue!\'\'}</td>\r\n							</#if>\r\n							</#list>\r\n						  </#if>\r\n					  </#if>\r\n					  <#assign rows=rows + title.rowspan>\r\n				  </#if>\r\n			</#list>\r\n	</#list>\r\n	<#else>\r\n	  <#list values as value>\r\n		<td class=\"row \" style=\"text-align: right;<#if value.valueType?? && value.valueType == \'total\'>background-color:#c5daed;</#if>\">\r\n		  ${value.valueStyle!value.foramatValue!\'\'}</td>\r\n	  </#list>\r\n	  </#if>\r\n	  </tr>\r\n	  </#list>\r\n	  </#if>\r\n  </tbody>\r\n</table>\r\n<#if reportModel?? && reportModel.isloadfulldata == \'true\'>\r\n<#if reportData?? && reportData.total gt reportData.pageSize>\r\n<div class=\"row\" style=\"padding:5px;\">\r\n	<div class=\"col-lg-12\" id=\"page_${reportModel.id!\'\'}\" style=\"text-align:center;\"></div>\r\n</div>\r\n</#if>\r\n<script>\r\n	layui.use([\'laypage\', \'layer\',\'form\'], function(){\r\n		  var laypage = layui.laypage\r\n		  ,layer = layui.layer;\r\n		  laypage.render({\r\n			  elem:\'page_${reportModel.id!\'\'}\'\r\n				,count: <#if reportData??>${reportData.total}<#else>0</#if> \r\n				,curr:<#if reportData??>${reportData.page}<#else>0</#if>\r\n				,groups: 5 \r\n				,limit:<#if reportData?? && reportData.pageSize gt 0>${reportData.pageSize}<#else>20</#if>\r\n				,jump:function(data , first){\r\n					if(!first){\r\n						loadURL(\"/apps/report/design/getelement.html?id=<#if reportModel??>${reportModel.id!\'\'}</#if>&p=\"+data.curr,\"#${reportModel.id!\'\'}\");\r\n					}\r\n				}\r\n		   });\r\n		});\r\n</script>\r\n</#if>\r\n<#else>\r\n<div class=\"layui-form\">\r\n  <table class=\"layui-table\">\r\n    <colgroup><col width=\"20%\"><col width=\"20%\"><col width=\"60%\"><col></colgroup>\r\n    <thead>\r\n      <tr><th>人物</th><th>民族</th><th>格言</th></tr> \r\n    </thead>\r\n    <tbody>\r\n      <tr><td>贤心</td><td>汉族</td><td>人生似修行</td>\r\n      </tr>\r\n      <tr><td>张爱玲</td><td>汉族</td><td>于千万人之中遇见你所遇见的人，于千万年之中，时间的无涯的荒野里…</td></tr>\r\n      <tr><td>Helen Keller</td><td>拉丁美裔</td><td> Life is either a daring adventure or nothing.</td></tr>\r\n    </tbody>\r\n  </table>\r\n</div>\r\n</#if>', '4028811b618d0dca01618d5a5fe6034a', 'ukewo', '/images/design/table.png', null, null, null, null, '0', 'simple', null);
INSERT INTO `uk_templet` VALUES ('4028811b6191e289016191e76714033e', '金字塔图', null, 'report', null, '2018-02-14 09:21:57', null, null, '<div id=\"chart_${element.mid!\'\'}\" ></div>\r\n<div id=\"json_${element.mid!\'\'}\" style=\"display:none;\">${reportModel.chartPropertiesJson}</div>\r\n	<script>\r\n	<#if reportData??>\r\n	var data_${element.mid!\'\'} = [];\r\n		<#if reportData.row??>\r\n			<#list reportData.row.title as title>\r\n				<#if (title_index+1) == reportData.row.title?size>\r\n					<#list title as rowtl>\r\n						var obj_${element.mid!\'\'} = new Object();\r\n						obj_${element.mid!\'\'}[\"_name\"] = \"${rowtl.rename!rowtl.name!\'\'}\";\r\n						<#if reportData.col??>\r\n						<#list reportData.col.title as coltlist>\r\n							<#list coltlist as coltl>\r\n								<#if reportData?? && reportData.data??>\r\n									<#list reportData.data as values>\r\n										<#if rowtl_index == values_index>\r\n											<#list values as val>	\r\n												<#if coltl_index == val_index && val_index == 0>\r\n													obj_${element.mid!\'\'}[\'key\'] = ${val.valueStyle!val.foramatValue!\'\'}\r\n												</#if>\r\n												<#if coltl_index == val_index && val_index == 1>\r\n													obj_${element.mid!\'\'}[\'value\'] = ${val.valueStyle!val.foramatValue!\'\'}\r\n												</#if>\r\n											</#list>\r\n										</#if>\r\n									</#list>\r\n								</#if>\r\n							</#list>\r\n						</#list>	\r\n						</#if>\r\n						data_${element.mid!\'\'}.push(obj_${element.mid!\'\'})						\r\n					</#list>			\r\n				</#if>\r\n			</#list>\r\n		</#if>\r\n		<#else>\r\n		const data_${element.mid!\'\'} = [\r\n					{ key: \'浏览网站\', value: 50000 },\r\n					{ key: \'放入购物车\', value: 35000 },\r\n					{ key: \'生成订单\', value: 25000 },\r\n					{ key: \'支付订单\', value: 15000 },\r\n					{ key: \'完成交易\', value: 8000 }\r\n				  ];		\r\n		</#if>	\r\n	  const dv_${element.mid!\'\'} = data_${element.mid!\'\'};\r\n	var json_${element.mid!\'\'} =  $.parseJSON( $(\"#json_${element.mid!\'\'}\").text() );\r\n	  $(\"#json_${element.mid!\'\'}\").remove();\r\n	  ChartAction.renderChart(\'${element.mid!\'\'}\',dv_${element.mid!\'\'},json_${element.mid!\'\'})\r\n	</script>\r\n', '4028811b618d0dca01618d5a5fe6034a', 'ukewo', '/images/design/jzt.png', null, null, null, null, '0', null, 'pyramid');
